  //> shipMap  : scala.collection.immutable.Map[String,String] = Map(Kirk -> Enterprise, 
  //| Picard -> Enterprise-D, Sisko -> Deep Space Nine, Janeway -> Voyager)

  println(shipMap("Janeway"))                       
  //> Voyager

  // Dealing with missing keys
